import { useState } from "react"

const CourseForm = () => {
    const [courseDetails, setCourseDetails] = useState({
        title: '',
        description: '',
        instructor: '',
        duration: 0,
        availableSlots: 0
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCourseDetails((prevDetails) => ({
          ...prevDetails,
          [name]: value,
        }));
      };
