import { useState } from "react"

const CourseForm = () => {
    const [courseDetails, setCourseDetails] = useState({
        title: '',
        description: '',
        instructor: '',
        duration: 0,
        availableSlots: 0
    })

    const handleChange = () => {
        fetch('/api/courses', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                title: title,
                description: description,
                instructor: instructor,
                duration: duration,
                availableSlots
            })
        })
    }

    return (
        <div>
            <h1>Add New Course</h1>
            <input type="text" placeholder="Title" value = {courseDetails.title} onChange={handleChange}/>
            <input type="text" placeholder="Description" value = {courseDetails.description} onChange={handleChange}/>
            <input type="text" placeholder="Instructor" value = {courseDetails.instructor} onChange={handleChange}/>
            <input type="text" placeholder="Duration" value = {courseDetails.duration} onChange={handleChange}/>
            <input type="text" placeholder="Available Slots" value = {courseDetails.availableSlots} onChange={handleChange}/>
        </div>
    )
}

export default CourseForm
