const CourseForm = () => {
    const [courseDetails, setCourseDetails] = useState({
        title: '',
        description: '',
        instructor: '',
        duration: 0,
        availableSlots: 0
    })

    return (
        <div>
            <h1>Add New Course</h1>
            <input type="text" placeholder="Title" value = {title} onChange={(e) => setCourseDetails(e.target.value)}/>
            <input type="text" placeholder="Description" value = {description}/>
            <input type="text" placeholder="Instructor" value = {instructor}/>
            <input type="text" placeholder="Duration" value = {duration}/>
            <input type="text" placeholder="Available Slots" value = {availableSlots}/>
        </div>
    )
}

export default CourseForm
