import { useState, useEffect } from 'react';

const CourseList = () => {
    const [courses, setCourses] = useState([])

    useEffect(()=> {
        fetch('/api/courses')
            .then((res) => res.json())
            .then((data) => {
                setCourses(data)
            })
            .catch((error) => console.error(error))
    }, [setCourses])

    return (
        <div>
            <h1>Course List</h1> 
            <ul>
                { courses && courses.map((course) => (
                    <li key={course._id}>
                        <p>Title</p>
                        <p>Description</p>
                        <p>Instructor</p>
                        <p>Duration</p>
                        <p>Available Slot</p>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default CourseList;
